  static const List<String> mediumPrayers = [];

  static const List<String> longPrayers = [];

  static List<String> getPrayers(String prayerSize) {
    switch (prayerSize) {
      case 'short':
        return shortPrayers;
      case 'medium':
        return mediumPrayers;
      case 'long':
        return longPrayers;
      case 'all':
        return shortPrayers + mediumPrayers + longPrayers;
      default:
        throw Exception('Invalid prayer length category');
    }
  }
